from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

class PyTorch_Transformers_LayoutLMv2_CIVQA_DVQA(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        multi_gpu = self.config.pop("_multi_gpu", False)

        model_id = "Sharka/CIVQA_DVQA_LayoutLMv2"
        self.processor = LayoutLMv2Processor.from_pretrained(model_id)
        self.model = LayoutLMv2ForQuestionAnswering.from_pretrained(model_id)

    def preprocess(self, input_document_images_and_questions):
        images = []
        questions = []
        for image_path, question in input_document_images_and_questions:
            images.append(Image.open(image_path).convert("RGB"))
            questions.append(question)
        
        encoding = self.processor(images, questions, return_tensors="pt", padding="max_length", truncation=True)
        return encoding

    def predict(self, model_input):
        outputs = self.model(**model_input)
        # Pass input_ids to postprocess for decoding the answer
        outputs['input_ids'] = model_input['input_ids']
        return outputs

    def postprocess(self, model_output):
        input_ids = model_output.pop('input_ids')
        start_logits = model_output.start_logits
        end_logits = model_output.end_logits

        start_indices = torch.argmax(start_logits, dim=1)
        end_indices = torch.argmax(end_logits, dim=1)

        answers = []
        for i in range(len(start_indices)):
            start_idx = start_indices[i].item()
            end_idx = end_indices[i].item()
            
            if start_idx > end_idx:
                answers.append("")
                continue

            answer_ids = input_ids[i][start_idx : end_idx + 1]
            answer = self.processor.tokenizer.decode(answer_ids, skip_special_tokens=True)
            answers.append(answer.strip())
            
        return answers
