# Generated by automation script
from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass
from transformers import LayoutXLMProcessor, LayoutLMv2ForQuestionAnswering
from PIL import Image
import easyocr
import numpy as np
import torch

class PyTorch_Transformers_LayoutXLM_CIVQA_EasyOCR(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        multi_gpu = self.config.pop("_multi_gpu", False)

        model_id = "Sharka/CIVQA_LayoutXLM_EasyOCR"
        self.processor = LayoutXLMProcessor.from_pretrained(model_id)
        self.model = LayoutLMv2ForQuestionAnswering.from_pretrained(model_id)

        # Initialize EasyOCR reader for Czech language
        # The model was fine-tuned on data processed with EasyOCR
        self.ocr_reader = easyocr.Reader(['cs'], gpu=(device == 'cuda'))

    def preprocess(self, input_document_images_and_questions):
        images, questions, all_words, all_boxes = [], [], [], []

        for image_path, question in input_document_images_and_questions:
            image = Image.open(image_path).convert("RGB")
            
            # Perform OCR using EasyOCR
            # The model expects words and their bounding boxes as input
            ocr_result = self.ocr_reader.readtext(np.array(image), detail=1)
            
            # Extract words and normalized bounding boxes
            words = [res[1] for res in ocr_result]
            boxes = []
            for res in ocr_result:
                # EasyOCR returns a box as [[x1,y1],[x2,y2],[x3,y3],[x4,y4]]
                # We need to convert it to [x_min, y_min, x_max, y_max]
                box_points = res[0]
                x_coords = [p[0] for p in box_points]
                y_coords = [p[1] for p in box_points]
                boxes.append([min(x_coords), min(y_coords), max(x_coords), max(y_coords)])

            images.append(image)
            questions.append(question)
            all_words.append(words)
            all_boxes.append(boxes)

        # Process the batch of images, questions, and OCR results
        encoding = self.processor(images, questions, words=all_words, boxes=all_boxes, return_tensors="pt", padding="max_length", truncation=True)
        return encoding

    def predict(self, model_input):
        outputs = self.model(**model_input)
        # Pass both model output and input_ids to postprocess for decoding the answer span
        return {"outputs": outputs, "input_ids": model_input["input_ids"]}

    def postprocess(self, model_output):
        outputs = model_output["outputs"]
        input_ids = model_output["input_ids"]
        
        start_logits = outputs.start_logits
        end_logits = outputs.end_logits

        answers = []
        # Iterate over each item in the batch
        for i in range(len(start_logits)):
            # Find the token indices with the highest probability for start and end of the answer
            start_index = torch.argmax(start_logits[i])
            end_index = torch.argmax(end_logits[i])
            
            # Get the input_ids for the current example
            current_input_ids = input_ids[i]
            
            # Decode the token ids corresponding to the answer span
            answer_tokens = current_input_ids[start_index : end_index + 1]
            answer = self.processor.tokenizer.decode(answer_tokens).strip()
            answers.append(answer)
        
        return answers
