from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

from transformers import pipeline
from PIL import Image

class PyTorch_Transformers_LayoutLM_Invoices(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        # The pipeline API does not support device_map="auto" for multi-gpu in the same way
        # as from_pretrained. It will run on the single specified device.
        self.config.pop("_multi_gpu", False)

        model_id = "aslessor/layoutlm-invoices"

        # The pipeline expects device as an integer index, -1 for CPU
        device_id = -1
        if device.startswith("cuda"):
            try:
                device_id = int(device.split(":")[1])
            except (IndexError, ValueError):
                device_id = 0  # Default to cuda:0 if no index is specified

        self.pipe = pipeline("document-question-answering", model=model_id, device=device_id)

    def preprocess(self, input_document_images_and_questions):
        inputs = []
        for image_path, question in input_document_images_and_questions:
            image = Image.open(image_path).convert("RGB")
            inputs.append({"image": image, "question": question})
        return inputs

    def predict(self, model_input):
        return self.pipe(model_input)

    def postprocess(self, model_output):
        # The pipeline returns a list of dicts, one for each input image-question pair.
        # Each dict contains the answer, score, etc.
        # We just need to extract the 'answer' string from each result.
        if not isinstance(model_output, list):
            model_output = [model_output]
        return [result['answer'] for result in model_output]
