    def predict(self, model_input):
        pil_images = model_input
        num_images = len(pil_images)
        num_labels = len(self.prompts)

        # Create pairs of (image, prompt) for efficient batch processing
        # Repeat each image for every prompt
        images_to_process = [img for img in pil_images for _ in self.prompts]
