from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

from transformers import CLIPProcessor, CLIPModel
from PIL import Image
import torch

class PyTorch_Transformers_CLIP_ViT_B_16_DataComp(PyTorchAbstractClass):
    def __init__(self, config=None):
        super().__init__(config)
        model_id = "laion/CLIP-ViT-B-16-DataComp.XL-s13B-b90K"

        self.processor = CLIPProcessor.from_pretrained(model_id)
        self.model = self.load_hf_model(CLIPModel, model_id)
        self.model.eval()

        # Download and prepare ImageNet labels for zero-shot classification
        features_file_url = "http://s3.amazonaws.com/store.carml.org/synsets/imagenet/synset.txt"
        self.features_path = self.features_download(features_file_url)
        with open(self.features_path, "r") as f:
            labels_text = [line.strip() for line in f.readlines()]
        
        # Use standard CLIP text prompt format
        self.labels = [f"a photo of a {label}" for label in labels_text]

        # Pre-compute text features
        text_inputs = self.processor(text=self.labels, return_tensors="pt", padding=True)
        if self.use_cuda:
            text_inputs = {k: v.to(self.device) for k, v in text_inputs.items()}

        with torch.no_grad():
            self.text_features = self.model.get_text_features(**text_inputs)

    def preprocess(self, input_images):
        processed_images = [
            Image.open(image_path).convert('RGB')
            for image_path in input_images
        ]
        model_input = self.processor(images=processed_images, return_tensors="pt")
        return model_input

    def predict(self, model_input):
        with torch.no_grad():
            # Move pixel values to the correct device
            pixel_values = model_input['pixel_values'].to(self.device)

            # Get image features
            image_features = self.model.get_image_features(pixel_values=pixel_values)

            # Normalized features
            image_features = image_features / image_features.norm(dim=-1, keepdim=True)
            text_features = self.text_features / self.text_features.norm(dim=-1, keepdim=True)

            # Cosine similarity as logits
            logit_scale = self.model.logit_scale.exp()
            logits_per_image = logit_scale * image_features @ text_features.T

        return logits_per_image

    def postprocess(self, model_output):
        probabilities = torch.nn.functional.softmax(model_output, dim=1)
        return probabilities.tolist()
