# Generated by automation script
from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

from transformers import CLIPProcessor, CLIPModel
from PIL import Image
import torch

class PyTorch_Transformers_CLIP_ViT_Large_Patch14(PyTorchAbstractClass):
    def __init__(self, config=None):
        super().__init__(config)
        model_id = "openai/clip-vit-large-patch14"

        self.processor = CLIPProcessor.from_pretrained(model_id)
        self.model = self.load_hf_model(CLIPModel, model_id)
        self.model.eval()

        # As a zero-shot model, it can use any set of classes.
        # We'll use ImageNet as a default example set.
        features_file_url = "http://s3.amazonaws.com/store.carml.org/synsets/imagenet/synset.txt"
        self.features = self.features_download(features_file_url)
        self.text_prompts = [f"a photo of a {label}" for label in self.features]

        # Pre-compute text features for efficiency during postprocessing
        with torch.no_grad():
            text_inputs = self.processor(text=self.text_prompts, return_tensors="pt", padding=True, truncation=True)
            # Move inputs to the correct device before getting features
            text_inputs = {k: v.to(self.device) for k, v in text_inputs.items()}
            self.text_features = self.model.get_text_features(**text_inputs)
            self.text_features /= self.text_features.norm(p=2, dim=-1, keepdim=True)

    def preprocess(self, input_images):
        processed_images = [
            Image.open(image_path).convert('RGB')
            for image_path in input_images
        ]
        # The processor for CLIP can handle images and text, but here we only process images.
        model_input = self.processor(images=processed_images, return_tensors="pt")
        return model_input

    def predict(self, model_input):
        # For CLIP, the prediction step is to get the image features (embeddings).
        # The actual classification (comparison with text) happens in postprocessing.
        with torch.no_grad():
            image_features = self.model.get_image_features(pixel_values=model_input.pixel_values.to(self.device))
        return image_features

    def postprocess(self, model_output):
        # model_output contains the image features.
        # We now compare these with the pre-computed text features.
        with torch.no_grad():
            # Normalize image features
            image_features = model_output / model_output.norm(p=2, dim=-1, keepdim=True)

            # Compute cosine similarity (logits)
            # The model's logit_scale is a learned temperature parameter
            logit_scale = self.model.logit_scale.exp()
            logits_per_image = logit_scale * image_features @ self.text_features.t()

            # Apply softmax to get probabilities
            probabilities = logits_per_image.softmax(dim=-1)

        return probabilities.cpu().tolist()
