# Generated by automation script
from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass
import torch
from diffusers import ControlNetModel, StableDiffusionControlNetPipeline, UniPCMultistepScheduler
from PIL import Image
import cv2
import numpy as np

class PyTorch_Diffusers_ControlNetCanny(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        # multi_gpu is not directly used for pipelines but we pop it to maintain consistency
        multi_gpu = self.config.pop("_multi_gpu", False)

        controlnet_model_id = "lllyasviel/control_v11p_sd15_canny"
        sd_model_id = "runwayml/stable-diffusion-v1-5"

        self.low_threshold = self.config.get('low_threshold', 100)
        self.high_threshold = self.config.get('high_threshold', 200)
        self.num_inference_steps = self.config.get('num_inference_steps', 20)
        self.seed = self.config.get('seed', 33)

        controlnet = ControlNetModel.from_pretrained(controlnet_model_id, torch_dtype=torch.float16)
        self.pipeline = StableDiffusionControlNetPipeline.from_pretrained(
            sd_model_id,
            controlnet=controlnet,
            torch_dtype=torch.float16
        )
        self.pipeline.scheduler = UniPCMultistepScheduler.from_config(self.pipeline.scheduler.config)
        # enable_model_cpu_offload is an alternative to placing the whole pipeline on one device
        # self.pipeline.enable_model_cpu_offload()

    def preprocess(self, input_images_and_prompts):
        control_images = []
        prompts = []
        for img_path, prompt in input_images_and_prompts:
            image = Image.open(img_path).convert("RGB")
            image_np = np.array(image)

            # Get Canny edges
            canny_image = cv2.Canny(image_np, self.low_threshold, self.high_threshold)
            canny_image = canny_image[:, :, None]
            canny_image = np.concatenate([canny_image, canny_image, canny_image], axis=2)
            control_image = Image.fromarray(canny_image)

            control_images.append(control_image)
            prompts.append(prompt)

        return {"image": control_images, "prompt": prompts}

    def predict(self, model_input):
        generator = torch.Generator(device=self.device).manual_seed(self.seed)
        output = self.pipeline(
            **model_input,
            num_inference_steps=self.num_inference_steps,
            generator=generator
        )
        return output.images

    def postprocess(self, model_output):
        # The pipeline output is a list of PIL Images
        return [np.array(img).tolist() for img in model_output]

    def to(self, device):
        self.device = device
        self.pipeline.to(device)

    def eval(self):
        # Pipelines manage their own state, but this method is here for compatibility
        pass

