class PyTorch_Diffusers_ControlNet_SoftEdge(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        multi_gpu = self.config.pop("_multi_gpu", False)

        controlnet_model_id = "lllyasviel/control_v11p_sd15_softedge"
        sd_model_id = "runwayml/stable-diffusion-v1-5"

        # Load the HED detector for preprocessing
        self.processor = HEDdetector.from_pretrained('lllyasviel/Annotators')

        # Load the ControlNet model
        # Using float16 for memory efficiency as shown in the model card example
        controlnet = ControlNetModel.from_pretrained(controlnet_model_id, torch_dtype=torch.float16)

        # Load the main Stable Diffusion pipeline with the ControlNet
        self.pipeline = StableDiffusionControlNetPipeline.from_pretrained(
            sd_model_id,
            controlnet=controlnet,
            torch_dtype=torch.float16
        )

        # Set up the scheduler as recommended in the model card
        self.pipeline.scheduler = UniPCMultistepScheduler.from_config(self.pipeline.scheduler.config)

        # Set seed for reproducibility
        self.seed = self.config.get('seed', 0)

    def preprocess(self, input_images_and_prompts):
        images = []
        prompts = []
        for img_path, prompt in input_images_and_prompts:
            # Load the user-provided image
            init_image = Image.open(img_path).convert("RGB")
            
            # Process the image to get the soft edge map (control image)
            control_image = self.processor(init_image)
            
            images.append(control_image)
            prompts.append(prompt)
            
        return {"image": images, "prompt": prompts}

    def predict(self, model_input):
        # The generator needs to be created on the correct device, which is handled in the to() method
        model_input['generator'] = self.generator
        model_input['num_inference_steps'] = self.config.get('num_inference_steps', 30)
        return self.pipeline(**model_input).images

    def postprocess(self, model_output):
        # The pipeline returns a list of PIL images, convert them to numpy arrays and then to lists for JSON serialization
        return [np.array(img).tolist() for img in model_output]

    def to(self, device):
        self.device = device
        self.pipeline.to(device)
        # Create a generator for reproducibility and ensure it's on the correct device
        self.generator = torch.Generator(device=device).manual_seed(self.seed)

