from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

import torch
from diffusers import DiffusionPipeline
from PIL import Image
import numpy as np
from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

class PyTorch_Diffusers_Flux_IP_Adapter(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        multi_gpu = self.config.pop("_multi_gpu", False) # multi_gpu is not explicitly used for single pipeline but good practice

        base_model_id = "black-forest-labs/FLUX.1-dev"
        ip_adapter_id = "XLabs-AI/flux-ip-adapter-v2"

        # FLUX models work best with bfloat16
        self.pipeline = DiffusionPipeline.from_pretrained(base_model_id, torch_dtype=torch.bfloat16)
        
        # Load the IP-Adapter weights
        # The file structure is XLabs-AI/flux-ip-adapter-v2/ip_adapter/ip_adapter.bin
        self.pipeline.load_ip_adapter(ip_adapter_id, subfolder="ip_adapter", weight_name="ip_adapter.bin")

    def preprocess(self, input_images_and_prompts):
        images = []
        prompts = []
        for img_path, prompt in input_images_and_prompts:
            # The IP-Adapter needs a reference image
            images.append(Image.open(img_path).convert('RGB'))
            prompts.append(prompt)
        
        # The pipeline expects the reference image under the 'ip_adapter_image' key
        return {"prompt": prompts, "ip_adapter_image": images}

    def predict(self, model_input):
        # The pipeline will use the prompt for text guidance and the ip_adapter_image for style/content guidance
        # The number of inference steps can be passed as a parameter if needed
        num_inference_steps = self.config.get('num_inference_steps', 50)
        guidance_scale = self.config.get('guidance_scale', 7.5)
        
        output = self.pipeline(
            **model_input,
            num_inference_steps=num_inference_steps,
            guidance_scale=guidance_scale
        )
        return output.images

    def postprocess(self, model_output):
        # The pipeline output is a list of PIL Images
        # Convert them to a JSON-serializable format (list of lists)
        return [np.array(img).tolist() for img in model_output]

    def to(self, device):
        self.device = device
        self.pipeline.to(self.device)

    def eval(self):
        # Set the pipeline to evaluation mode
        self.pipeline.to(self.device) # Ensure it's on the correct device before eval
