        generator = torch.Generator(device=self.device).manual_seed(self.seed)
        # The LoRA scale is passed via cross_attention_kwargs
        return self.pipeline(
            **model_input, 
            generator=generator,
            cross_attention_kwargs={"scale": self.lora_scale}
        ).images

