# Generated by automation script
from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

import torch
from diffusers import StableDiffusionControlNetPipeline, ControlNetModel, UniPCMultistepScheduler
from PIL import Image
import numpy as np
import cv2

class PyTorch_Diffusers_ControlNetCanny(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        multi_gpu = self.config.pop("_multi_gpu", False)

        # The model card for 'refiners/sd15.controlnet.canny' specifies the base model and adapter.
        # We load these components to build the standard diffusers pipeline.
        base_model_id = "runwayml/stable-diffusion-v1-5"
        controlnet_id = "lllyasviel/control_v11p_sd15_canny"

        # Use float16 for memory efficiency
        dtype = torch.float16 if device == "cuda" else torch.float32

        controlnet = ControlNetModel.from_pretrained(controlnet_id, torch_dtype=dtype)
        
        if multi_gpu and device == "cuda":
            self.pipeline = StableDiffusionControlNetPipeline.from_pretrained(
                base_model_id, controlnet=controlnet, torch_dtype=dtype, device_map="auto"
            )
        else:
            self.pipeline = StableDiffusionControlNetPipeline.from_pretrained(
                base_model_id, controlnet=controlnet, torch_dtype=dtype
            )

        self.pipeline.scheduler = UniPCMultistepScheduler.from_config(self.pipeline.scheduler.config)
        self.seed = self.config.get('seed', 0)

    def preprocess(self, input_images_and_prompts):
        images = []
        prompts = []
        for img_path, prompt in input_images_and_prompts:
            # Load the image
            input_image = Image.open(img_path).convert('RGB')
            
            # Convert to numpy array for OpenCV processing
            np_image = np.array(input_image)
            
            # Get Canny edges
            low_threshold = 100
            high_threshold = 200
            canny_image = cv2.Canny(np_image, low_threshold, high_threshold)
            
            # Convert single-channel Canny output to a 3-channel image
            canny_image = canny_image[:, :, None]
            canny_image = np.concatenate([canny_image, canny_image, canny_image], axis=2)
            
            # Convert back to PIL Image
            control_image = Image.fromarray(canny_image)
            
            images.append(control_image)
            prompts.append(prompt)
            
        return {"image": images, "prompt": prompts}

    def predict(self, model_input):
        return self.pipeline(**model_input, generator=self.generator).images

    def postprocess(self, model_output):
        # The pipeline returns a list of PIL images, which we convert to numpy arrays and then to lists
        return [np.array(img).tolist() for img in model_output]

    def to(self, device):
        self.device = device
        self.pipeline.to(device)
        self.generator = torch.Generator(device=device).manual_seed(self.seed)

    def eval(self):
        # Pipelines manage their own evaluation mode, but this is good practice
        self.pipeline.controlnet.eval()
        self.pipeline.vae.eval()
        self.pipeline.text_encoder.eval()
        self.pipeline.unet.eval()

