# Generated by automation script
from mlmodelscope.pytorch_agent.models.pytorch_abc import PyTorchAbstractClass

import torch
from diffusers import StableDiffusionControlNetPipeline, ControlNetModel, UniPCMultistepScheduler
from PIL import Image
import numpy as np

class PyTorch_Diffusers_ControlNetOpenPose(PyTorchAbstractClass):
    def __init__(self, config=None):
        self.config = config if config else dict()
        device = self.config.pop("_device", "cpu")
        multi_gpu = self.config.pop("_multi_gpu", False)

        model_id = "lllyasviel/sd-controlnet-openpose"
        base_model_id = "runwayml/stable-diffusion-v1-5"

        controlnet = ControlNetModel.from_pretrained(model_id, torch_dtype=torch.float16)
        self.pipeline = StableDiffusionControlNetPipeline.from_pretrained(
            base_model_id,
            controlnet=controlnet,
            torch_dtype=torch.float16
        )
        self.pipeline.scheduler = UniPCMultistepScheduler.from_config(self.pipeline.scheduler.config)
        # self.pipeline.enable_model_cpu_offload() # Enable this for low VRAM environments

    def preprocess(self, input_images_and_prompts):
        images = []
        prompts = []
        for img_path, prompt in input_images_and_prompts:
            # The input image for this ControlNet should be a pose estimation map
            images.append(Image.open(img_path).convert('RGB'))
            prompts.append(prompt)
        return {"image": images, "prompt": prompts}

    def predict(self, model_input):
        # The pipeline returns an object with an 'images' attribute, which is a list of PIL images.
        # Additional parameters like num_inference_steps can be passed in self.config
        num_inference_steps = self.config.get('num_inference_steps', 20)
        output = self.pipeline(**model_input, num_inference_steps=num_inference_steps)
        return output.images

    def postprocess(self, model_output):
        # The model output is a list of PIL Image objects.
        # Convert them to numpy arrays and then to lists for JSON serialization.
        return [np.array(img).tolist() for img in model_output]

    def to(self, device):
        self.pipeline.to(device)
        # Optional: Enable xformers for memory-efficient attention if available
        try:
            self.pipeline.enable_xformers_memory_efficient_attention()
        except ImportError:
            print("xformers not installed, continuing without it")

    def eval(self):
        # Diffusers pipelines are already in eval mode by default.
        pass

