    def preprocess(self, input_videos):
        container = av.open(input_videos[0])
        
        # Sample 16 frames uniformly from the video
        total_frames = container.streams.video[0].frames
        if total_frames == 0:
            raise ValueError("Video has no frames")
        indices = np.linspace(0, total_frames - 1, 16, dtype=int)

        # Read the frames at the sampled indices
        frames = []
